import { StyleSheet } from 'aphrodite/no-important'

export default StyleSheet.create({
    login: {
        width: '200px', /* Ширина элемента в пикселах */
        margin: 'auto', /* Выравниваем по центру */
        marginTop: '10%',
    },
    Input: {
        display: 'flex',
        flexWrap: 'wrap',
        margin: '0 auto',
        alignItems: 'center',
        padding: '0 5%'
    }
})
