import React, { Component } from 'react'
import Navbar from './navbar'
import { css } from 'aphrodite'
import styles from '../styles/styles' 
import Axios from 'axios';
import { Item } from 'semantic-ui-react';

class Fl extends Component {
    state = {}
    componentDidMount(){
        var self = this
        this.setState({['comp']: []})
        if(localStorage.getItem('session') == "true")
        {
            var pl = new FormData();
            pl.set('get', 'post');
            Axios({
                method: 'post',
                url: 'http://localhost:8888/back/getFl.php',
                data: pl,
                config: { headers: {'Content-Type': 'multipart/form-data' }}
            }).then(function(response){
                console.log(response)
                self.setState({['cmp']: response.data})
                for(var i = 0; i < response.data.length; i++)
                {
                    var pl = new FormData();
                    pl.set('comp', response.data[i].company)
                    Axios({
                        method: 'post',
                        url: 'http://localhost:8888/back/getCompInf.php',
                        data: pl,
                        config: { headers: {' Content-Type': 'multipart/form-data' }}
                    }).then(function(responseS){
                        self.setState({[response.data[i].company]: responseS.data})
                    }).catch(function(error){
                        console.log(error)
                    })
                }
            }).catch(function(error){
                console.log(error)
            })
        }
    }
    render() {
        if(localStorage.getItem('session') == "true")
        {
            return (
                <div>
                    <Navbar />
                    <Item.Group>

                    </Item.Group>
                </div>
            )
        }
        else
        {
            return (
                <div>
                    <Navbar />
                    <div className={css(styles.log)}>
                        <h1>Пожалуйста <a href='#/login'>авторизируйтесь</a>, или <a href='#/reg'>зарегестрируйтесь</a></h1>
                    </div>
                </div>
            )
        }
    }
}

export default Fl;
